import {useState} from "react"
import { withRouter } from "react-router-dom"
import { Table, Tag, Space ,Button, Tabs, Divider } from 'antd';

import InvestmentDetail from "./../components/InvestmentDetail"




const columns = [
  {
    title: '名称',
    dataIndex: 'name',
    key: 'name',
    render: text => <a>{text}</a>,
  },
  {
    title: '收益代币',
    dataIndex: 'earnings',
    key: 'earnings',
  },
  {
    key: 'earn',
    render:()=>(
        <Button type="primary">EARN</Button>
    )
  },
  {
    key: 'tags',
    render:()=>(
        <Button type="primary">EARN</Button>
    )
  },
  {
    dataIndex: 'tags',
    key: 'tags',
    render:(props,params)=>(
        <Button 
        onClick={()=>{
            setShowDrawer(true)
        }} 
        type="primary">︾</Button>
    )
  }
];

const data = [
  {
    key: '1',
    name: 'John Brown',
    age: 32,
    address: 'New York No. 1 Lake Park',
    tags: ['nice', 'developer'],
  },
  {
    key: '2',
    name: 'Jim Green',
    age: 42,
    address: 'London No. 1 Lake Park',
    tags: ['loser'],
  },
];







function Investment (props) {
    const [ showDrawer , setShowDrawer ] = useState(false)
    return(
        <div>
            <Table columns={columns} dataSource={data} />
            <InvestmentDetail />
        </div>


    )
}

export default Investment
