const layout = {
    layout:"inline",
  };
  const tailLayout = {
    wrapperCol: {
      offset: 20,
      span: 16,
    },
  };


const IFrom = () => {
    const onFinish = (values) => {
        console.log('Success:', values);
      };
    return(
        <Form
        layout="inline"
        onFinish={onFinish}
        >
            <Form.Item
            label="脚本编号"
            name="scriptCode"
            rules={[{ message: 'Please input your username!' }]}
            >
                <Input />
            </Form.Item>
            <Form.Item
            label="运行结果"
            name="theResults"
            rules={[{ message: 'Please input your username!' }]}
            >
                {/* <Input /> */}
                <Select
                value={value.currency || currency}
                style={{
                width: 80,
                margin: '0 8px',
                }}
                onChange={onCurrencyChange}
                >
                    <Option value="rmb">RMB</Option>
                    <Option value="dollar">Dollar</Option>
                </Select>
            </Form.Item>
            <Form.Item
            label="运行状态"
            name="runningState"
            rules={[{ message: 'Please input your username!' }]}
            >
                <Input />
            </Form.Item>
            <Form.Item {...tailLayout}>
                <Button type="primary" htmlType="submit">
                Submit
                </Button>
            </Form.Item>
        </Form>
    )
}


const dataSource = [
    {
        key: '1',
        scriptCode: '胡彦斌',
        scriptName: 32,
        runningState: '西湖区湖底公园1号',
        theResults: "成功",
        lastRunTime: "11-0-1",
        tunButton: (<button>运行</button>),
        open: (<button onClick={(e)=>{
            e.stopPropagation()
        }}>运行</button>),
        description: '这是脚本的详情'
    },
    {
        key: '2',
        scriptCode: '胡彦祖',
        scriptName: 42,
        runningState: '西湖区湖底公园1号',
        theResults: "成功",
        lastRunTime: "11-0-1",
        tunButton: (<button onClick={(e)=>{
            e.stopPropagation()
        }}>运行</button>),
        open: (<button>打开</button>)
    },
];

const columns = [
    {
        title: '脚本编号',
        dataIndex: 'scriptCode',
        key: 'scriptCode',
    },
    {
        title: '脚本名称',
        dataIndex: 'scriptName',
        key: 'scriptName',
    },
    {
        title: '运行状态',
        dataIndex: 'runningState',
        key: 'runningState',
    },
    {
        title: '运行结果',
        dataIndex: 'theResults',
        key: 'theResults',
    },
    {
        title: "上次运行时间",
        dataIndex: "lastRunTime",
        key: "lastRunTime"
    },
    {
        dataIndex: "tunButton",
        key: "tunButton"
    },
    {
        dataIndex: "open",
        key: "open"
    }
];

function AutomaticInvestment() {
    const [ timeDomShow , setTimeDomShow ] = useState(false)
    const dataSourceHander = () => {
        return dataSource.map( item=>(
            {
                key: item.key,
                scriptCode: item.scriptCode,
                scriptName: item.scriptName,
                runningState: item.runningState,
                theResults: item.theResults,
                lastRunTime: item.lastRunTime,
                open: (<button>运行</button>),
                tunButton: (<button onClick={(e)=>{
                    console.log(111)
                    e.stopPropagation()
                }}>运行</button>),
                description: item.description
            }
        ) )
    }
    return (
        <div>
            <div className="prevStateShow">
                { 
                    timeDomShow 
                    ? (
                        <div className="prevStateShow-children">
                            <div>上次运行时间</div>
                            <div>1</div>
                        </div>
                    ) 
                    : null
                }
                <div className="prevStateShow-children">
                    <div>上次运行成功</div>
                   <div>1</div>
                </div>
                <div className="prevStateShow-children">
                    <div>上次运行失败</div>
                    <div>1</div>
                </div>
            </div>
            <IFrom />

            <Table
                expandable={{
                    expandRowByClick: true,
                    expandedRowRender: record => (
                        <div>{record.description}</div>
                    ),
                    rowExpandable: record => record.name !== 'Not Expandable',
                    onExpandedRowsChange:()=>{
                        setTimeDomShow(!timeDomShow)
                    }
                }}
                dataSource={dataSource}
                columns={columns} />
        </div>

    )
}

export default AutomaticInvestment
