import Web3 from "web3"

if (window.ethereum) {
    var web3 = new Web3(window.ethereum);
}

const accounts = ""
new Promise( 
    web3.eth.requestAccounts() 
).then(res=>{
    accounts = res
})
const account = accounts[0];

export {
    web3,
    account
}
