import { Link , Switch , Route , Redirect } from "react-router-dom"

import React from "react"
import { Layout, Menu, Breadcrumb } from 'antd';
// import {
//   DesktopOutlined,
//   PieChartOutlined
// } from '@ant-design/icons';

import "./index.css"
import menus from "./../../router/menus"


const { Header, Content, Sider } = Layout;

export default class Index extends React.Component {
    constructor(props){
        super( props )
    }
    state = {
      collapsed: false,
    };

    clickHandler = ( path ) => {
        console.log(this.props)
    }

    onCollapse = collapsed => {
      console.log(collapsed);
      this.setState({ collapsed });
    };

    render() {
      const { collapsed } = this.state;
      return (
        <Layout style={{ minHeight: '100vh' }}>
          <Sider collapsible collapsed={collapsed} onCollapse={this.onCollapse}>
            <div className="logo" />
            <Menu theme="dark" defaultSelectedKeys={['1']} mode="inline">
              {menus.map( item => (
                (item.meta && item.meta.hidden) ? 
                null :
                (   
                        <Menu.Item onClick={ ()=>{ this.clickHandler( item.path ) } } key={item.key} icon={item.icon}>
                            <Link to={item.path}>
                                {item.title}
                            </Link>
                        </Menu.Item>
                )) )}
            </Menu>
          </Sider>
          <Layout className="site-layout">
            <Header className="site-layout-background" style={{ padding: 0 }} />
            <Content style={{ margin: '0 16px' }}>
                <Switch>
                    {
                        menus.map( item=>(
                            item.redirect 
                            ? 
                            <Redirect key={item.key} path={item.path} to={item.redirect} exact></Redirect> 
                            : 
                            <Route key={item.key} exact path={item.path} component={item.component}></Route>
                        ) )
                    }
                </Switch>
            </Content>
          </Layout>
        </Layout>
      );
    }
  }
